import React from 'react';
import { connect } from 'react-redux'
import { INCREMENT, DECREMENT, EVENINCREMENT, ODDINCREMENT } from '../Store/actionTypes';

const Counter = (props) => {

  const handleIncrement = (e) => {
    props.increment()
  }

  const handleDecrement = (e) => {
    props.decrement()
  }

  const handleEvenIncrement = (e) => {
    props.evenIncrement()
  }

  const handleOddIncrement = (e) => {
    props.oddIncrement()
  }

  return (
    <div>
      <p>{props.counter}</p>
      <button onClick={handleIncrement}>+</button>
      <button onClick={handleDecrement}>-</button>
      <button onClick={handleEvenIncrement}>Even +</button>
      <button onClick={handleOddIncrement}>Odd +</button>

    </div>
  )
}

const mapStateToProps = (state) => {
  return state
}

const mapDispatchToProps = (dispatch) => {
  return {
    increment: () => {
      console.log('increment called')
      dispatch({
        type: INCREMENT
      })
    },
    decrement: () => {
      console.log('decrement called')
      dispatch({
        type: DECREMENT
      })
    },
    evenIncrement: () => {
      console.log('Even increment called')
      dispatch({
        type: EVENINCREMENT
      })
    },
    oddIncrement: () => {
      console.log('Odd increment called')
      dispatch({
        type: ODDINCREMENT
      })
    }
  }
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Counter)

